import React, { useState,useEffect} from 'react'
import { useNavigate } from 'react-router-dom'
import EmployeeService from '../services/EmployeeService';

const EmployeeList = () => {
const navigate = useNavigate();

const [loading, setLoading] = useState(true);
const [employees,setEmployees] = useState(null);

useEffect(() => {
    const fetchData = async () => {
        setLoading(true);
        try
        {
            const response = await EmployeeService.getEmployees();
            setEmployees(response.data);
        }
        catch(error)
        {
            console.log(error)
        }
        setLoading(false);

    };

    fetchData();
}, []);

  return (
<div className='container mx-auto my-8'>

    <div className='h-12'>
        <button onClick={ () => navigate("/addEmployee")} className='rounded bg-slate-600 text-white font-semibold px-6 py-2'>Add Employee</button>
    </div>

    <div className='flex shadow border-b'>
        <table className='min-w-full'>
            <thead className='bg-gray-100'>
                <tr>
                    <th className='text-left font-medium text-gray-600 uppercase tracking-wider py-3 px-6'>First Name</th>
                    <th className='text-left font-medium text-gray-600 uppercase tracking-wider py-3 px-6'>Last Name</th>
                    <th className='text-left font-medium text-gray-600 uppercase tracking-wider py-3 px-6'>Email ID</th>
                    <th className='text-right font-medium text-gray-600 uppercase tracking-wider py-3 px-6'>Actions</th>

                </tr>
            </thead>
        {!loading &&(

            <tbody className='bg-white'>

                <tr>
                <td className='text-left px-6 py-4 whitespace-nowrap'>
                    <div className='text-sm text-gray-500'>Rajiv</div>
                </td>
                <td className='text-left px-6 py-4 whitespace-nowrap'>
                    <div className='text-sm text-gray-500'>Nidumolu</div>
                </td>
                <td className='text-left px-6 py-4 whitespace-nowrap'>
                    <div className='text-sm text-gray-500'>nrajiv1997@gmail.com</div>
                </td>


                <td className='text-right px-6 py-4 whitespace-nowrap font-medium text-sm'>
                <a href='#' className='text-indigo-600 hover:text-indigo-1000 px-4'>UPDATE</a>
                <a href='#' className='text-red-600 hover:text-indigo-1000 px-4'>DELETE</a>
                </td>
                </tr>

            </tbody>)}

        </table>
    </div>
</div>
  ) 
}

export default EmployeeList
